(function(){'use strict';function setupSwipeGestures(){let touchStartX=0;let touchEndX=0;document.addEventListener('touchstart',function(e){touchStartX=e.changedTouches[0].screenX;},{passive:true});document.addEventListener('touchend',function(e){touchEndX=e.changedTouches[0].screenX;handleSwipe();},{passive:true});function handleSwipe(){const swipeThreshold=100;const sidebar=document.getElementById('sidebar');if(!sidebar)return;const isOpen=sidebar.classList.contains('show');if(touchEndX>touchStartX+swipeThreshold&&!isOpen&&touchStartX<50){if(window.toggleSidebar)window.toggleSidebar();}if(touchStartX>touchEndX+swipeThreshold&&isOpen){if(window.toggleSidebar)window.toggleSidebar();}}}function makeTablesResponsive(){const tables=document.querySelectorAll('table:not(.table-responsive table)');tables.forEach(table=>{if(table.parentElement.classList.contains('table-responsive')){return;}const wrapper=document.createElement('div');wrapper.className='table-responsive';table.parentNode.insertBefore(wrapper,table);wrapper.appendChild(table);});}function setupMobileButtons(){const actionButtons=document.querySelectorAll('.d-flex .btn,.text-end .btn');actionButtons.forEach(btn=>{if(window.innerWidth<576){btn.classList.add('btn-block-mobile');}});}function init(){if(document.readyState==='loading'){document.addEventListener('DOMContentLoaded',init);return;}setupSwipeGestures();makeTablesResponsive();setupMobileButtons();console.log('âœ… Mobile utilities inicializado');}init();window.FinanSmartMobile={makeTablesResponsive:makeTablesResponsive};})();